function Send-FormatedMail {
    [CmdLetBinding()]
    Param (
        [Parameter(Mandatory=$false)]
        [string]$From = 'sccm-noreply@sdworx.com'
        ,
        [Parameter(Mandatory=$true)]
        [string[]]$To
        ,
        [Parameter(Mandatory=$false)]
        [string[]]$CC
        ,
        [Parameter(Mandatory=$false)]
        [string]$TicketNumber
        ,
        [Parameter(Mandatory=$false)]
        [string]$SourceRunbook
        ,
        [Parameter(Mandatory=$false)]
        [string]$BackgroundColorBody = '#EEEEEE'
        ,
        [Parameter(Mandatory=$false)]
        [string]$BackgroundColorMessage = '#FFFFFF'
        ,
        [Parameter(Mandatory=$false)]
        [string]$BackgroundColorFooter = '#DCDCDC'
        ,
        [Parameter(Mandatory=$false)]
        [string]$BackgroundColorTitle
        ,
        [Parameter(Mandatory=$false)]
        [string]$BorderColor
        ,
        [Parameter(Mandatory=$false)]
        [ValidateSet('ERROR', 'WARNING', 'OK', 'INFO')]
        [string]$MessageType
        ,
        [Parameter(Mandatory=$true)]
        [string]$MessageTitle
        ,
        [Parameter(Mandatory=$false)]
        [string]$FontColorTitle = '#FFFFFF'
        ,
        [Parameter(Mandatory=$false)]
        [string]$FontColorMessage = '#000000'
        ,
        [Parameter(Mandatory=$false)]
        [string]$FontColorFooter = '#000000'
        ,
        [Parameter(Mandatory=$true)]
        [string]$Message
        ,
        [Parameter(Mandatory=$false)]
        [string]$FooterTextLeft = '<B>SD Worx</B><br>Server Infrastructure'
        ,
        [Parameter(Mandatory=$false)]
        [string]$FooterTextRight = '<a href="https://sd.sharepoint.com/sites/INT_CORP_Cloud_Technologies/SitePages/CloudTechnologies-Servers.aspx" style="color:blue;text-decoration: none;">About us</a><br><a href="supportdcsystems@sdworx.com" style="color:blue; text-decoration:none;">supportdcsystems@sdworx.com</a>'
        ,
        [Parameter(Mandatory=$false)]
        [string]$SourceTextLeft = 'Runbook: 3.1.2 - VM Provisioning POC'
        ,
        [Parameter(Mandatory=$false)]
        [string]$SourceTextRight
        ,
        [Parameter(Mandatory=$false)]
        [string]$BodyWidth = '800px'
    )
    if ( -not [boolean]$BorderColor ) {
        switch ( $MessageType ) {
            'ERROR' {
                $BorderColor = '#FF0000'
            }
            'WARNING' {
                $BorderColor = '#FFF200'
                $FontColorTitle = '#000000'
            }
            'OK' {
                $BorderColor = '#008000'
            }
            Default {
                $BorderColor = '#005EB8'
            }
        }
    }
    if ( -not [boolean]$BackgroundColorTitle )   { $BackgroundColorTitle = $BorderColor }
    if ( -not [boolean]$SourceTextRight )        {
        if ( [boolean]$SourceRunbook ) {
            $SourceTextRight = $SourceRunbook
        }
        else {
            $SourceTextRight = $env:COMPUTERNAME
        }
    }

    $SMTPServer = 'smtp.dika.be'
    if ( $MessageType ) {
        $Subject = "${MessageType}: ${MessageTitle}"
    }
    else {
        $Subject = $MessageTitle
    }
    Import-Module PSHTML

    #region image
        $ImageBase64 = ''
    #endregion image


    #region create html template
        $html = html -Content {
            body -Style "background-color: ${BackgroundColorBody};" -Content {
                Div -Attributes @{ align = 'center' } -Style "width: 99%; background-color: ${BackgroundColorBody};" -Content {
                    table -Class 'MsoNormalTable' -Attributes @{ border=0; cellspacing=0; cellpadding=0 } `
                        -Style "width: ${BodyWidth}; border: none; mso-cellspacing:0cm;" `
                        -Content {
                        tr -Content {
                            td -Attributes @{ colspan = 3 } `
                            -Style "font-size:1px; height: 10px;border-top: 1pt solid ${BorderColor}; border-left: 1pt solid ${BorderColor}; border-right: 1pt solid ${BorderColor}; background-color: ${BackgroundColorMessage};" `
                            -Content '&nbsp;'
                        }
                        tr -Content {
                            td -Attributes @{ colspan = 3 } `
                            -Style "height:80pt;background: ${BackgroundColorTitle};font-size:18.0pt;font-family:Calibri,'Trebuchet MS',sans-serif;color:${FontColorTitle}; text-align: center;" `
                            -Content $MessageTitle
                        }
                        tr -Content {
                            td -Attributes @{ colspan = 3 } `
                            -Style "font-size:11.0pt;font-family:Calibri,'Trebuchet MS',sans-serif;text-align: left;vertical-align:top;padding: 20pt 30.6pt 20pt 30.6pt; border-left: 1pt solid ${BorderColor}; border-right: 1pt solid ${BorderColor}; background-color: ${BackgroundColorMessage}; color: ${FontColorMessage}" `
                            -Content $Message
                        }
                        tr -Content {
                            td -Content {
                                img -src "data:image/gif;base64,${ImageBase64}" -alt 'SD Worx' -width 135 -height 80
                            } -Style "height: 80px; width: 115pt; font-family:'Trebuchet MS',sans-serif;font-size: 9pt; padding: 0pt 10.6pt 0pt 12pt; margin: 7.5pt 0cm; border-left: 1pt solid ${BorderColor}; background: ${BackgroundColorFooter}; color: white;"
                            td -Content $FooterTextLeft -Style "height: 80px; font-family:Calibri,'Trebuchet MS',sans-serif;font-size: 9pt; padding: 0pt 0pt 0pt 0pt; width: 150pt; margin: 7.5pt 0cm; background: ${BackgroundColorFooter}; color: ${FontColorFooter};"
                            td -Content $FooterTextRight -Style "height: 80px; font-family:Calibri,'Trebuchet MS',sans-serif;font-size: 9pt; border-right: 1pt solid ${BorderColor}; background: ${BackgroundColorFooter}; text-align: right;padding: 0pt 30.6pt 0pt 30.6pt; color: ${FontColorFooter};"
                        }
                        tr -Content {
                            td -Attributes @{ colspan = 3 } -Style "font-size:1px; height: 10px; border-left: 1pt solid ${BorderColor}; border-bottom: 1pt solid ${BorderColor}; border-right: 1pt solid ${BorderColor}; background-color: ${BackgroundColorMessage};" -Content '&nbsp;'
                        }
                        tr -Content {
                            td -Content '&nbsp;' -Style 'font-size:1px; height: 10px;'
                        }
                        tr -Content {
                            td -Content $SourceTextLeft -Style "padding: 3pt 30.6pt 3pt 30.6pt; font-family:Calibri,'Trebuchet MS',sans-serif;font-size:9px; height: 10px;background: ${BackgroundColorBody};" -Attributes @{ colspan=2 }
                            td -Content $SourceTextRight -Style "padding: 3pt 30.6pt 3pt 30.6pt; font-family:Calibri,'Trebuchet MS',sans-serif;font-size:9px; height: 10px;background: ${BackgroundColorBody}; text-align: right;"
                        }
                        tr -Content {
                            td -Content '&nbsp;'
                            td -Content '&nbsp;'
                            td -Content '&nbsp;'
                        }
                    }
                }
            }
        }

    #endregion create html template

    #region send message
        $MailParams = @{
            To = $To.Split(',')
            From = $From
            Body = $html
            Subject = $Subject
            SmtpServer = $SMTPServer
        }
        if ( [boolean]$CC ) {
            $MailParams.Add( 'CC', $CC.Split(',') )
        }

        Send-MailMessage -BodyAsHtml @MailParams
    #endregion send message
}